(function($) {
    $.fn.extend({
        diyUpload: function(opt, serverCallBack) {
            if (typeof opt != "object") {
                alert('params error!');
                return;
            }
            var $fileInput = $(this);
            var $fileInputId = $fileInput.attr('id');
            //var $fileInputId = 'myuploader';
            if (opt.url) {
                opt.server = opt.url;
                delete opt.url;
            }else{
                //如果没有上传地址就用默认的
                opt.server = 'upload.php'
            }
            if (opt.success) {
                var successCallBack = opt.success;
                delete opt.success;
            }
            if (opt.error) {
                var errorCallBack = opt.error;
                delete opt.error;
            }
            $.each(getOption('#' + $fileInputId),
            function(key, value) {
                opt[key] = opt[key] || value;
            });
            if (opt.buttonText) {
                opt['pick']['label'] = opt.buttonText;
                delete opt.buttonText;
            }
            var webUploader = getUploader(opt);
            if (!WebUploader.Uploader.support()) {
                alert('Your browser is not supported upload.');
                return false;
            }
            webUploader.on('fileQueued',
            function(file) {
                createBox($fileInput, file, webUploader);
            });
            webUploader.on('uploadProgress',
            function(file, percentage) {
                var $fileBox = $('#fileBox_' + file.id);
                var $diyBar = $fileBox.find('.diyBar');
                $diyBar.show();
                percentage = percentage * 100;
                showDiyProgress(percentage.toFixed(2), $diyBar);
            });
            webUploader.on('uploadFinished',
            function() {
                $fileInput.next('.parentFileBox').children('.diyButton').remove();
            });
            webUploader.on('uploadAccept',
            function(object, data) {
                if (serverCallBack) serverCallBack(data);
            });
            //上传成功
            webUploader.on('uploadSuccess',
            function(file, response) {
                console.log(response);
                var $fileBox = $('#fileBox_' + file.id);
                var $diyBar = $fileBox.find('.diyBar');
                showDiyProgress(100, $diyBar, 'Upload success.');
                /*new add $fileBox.removeClass('diyUploadHover');*/
                $diyBar.fadeOut(1000,
                function() {
                    $fileBox.children('.diySuccess').show();
                });

                $fileBox.find('.viewImg').attr("src",response.data.oss_url);
                var reviewPhotoText = new Array();
                $fileInput.parents('.upload-ul').children('.diyUploadHover').children('.viewThumb').children('.viewImg').each(function(){
                    reviewPhotoText.push($(this).attr("src"));
                });
                reviewPhotoText = reviewPhotoText.join(',');
                $fileInput.parents('.upload-ul').children('input').val(reviewPhotoText);
                if (successCallBack) {
                    successCallBack(response, $fileBox);
                }
            });
            webUploader.on('uploadError',
            function(file, reason) {
                var $fileBox = $('#fileBox_' + file.id);
                var $diyBar = $fileBox.find('.diyBar');
                showDiyProgress(0, $diyBar, 'Upload failed.');
                var err = 'Upload failed. file:' + file.name + ' error:' + reason;
                if (errorCallBack) {
                    errorCallBack(err);
                }
            });
            webUploader.on('error',
            function(code) {
                var text = '';
                switch (code) {
                case 'F_DUPLICATE':
                    text = 'The file has been selected.';
                    break;
                case 'Q_EXCEED_NUM_LIMIT':
                    text = 'The number of uploaded files exceeds the limit.';
                    break;
                case 'F_EXCEED_SIZE':
                    text = 'The file size exceeds the limit.';
                    break;
                case 'Q_EXCEED_SIZE_LIMIT':
                    text = 'The total size of all files exceeds the limit.';
                    break;
                case 'Q_TYPE_DENIED':
                    text = 'Invalid file type or empty file.';
                    break;
                default:
                    text = 'unknown error.';
                    break;
                }
                alert(text);
            });
            return webUploader;
        }
    });

    function getOption(objId) {
        return {
            auto: true,
            pick: {
                id: objId,
                label: ""
            },
            accept: {
                title: "Images",
                extensions: "gif,jpg,jpeg,bmp,png",
                mimeTypes: "image/*"
            },
            thumb: {
                width: 90,
                height: 90,
                quality: 90,
                allowMagnify: false,
                crop: true,
                type: "image/jpeg"
            },
            method: "POST",
            server: "",
            sendAsBinary: false,
            chunked: true,
            chunkSize: 512 * 1024,
            fileNumLimit: 1,
            fileSizeLimit: 10000 * 1024,
            fileSingleSizeLimit: 2000 * 1024
        };
    }
    function getUploader(opt) {
        return new WebUploader.Uploader(opt);
    }
    function showDiyProgress(progress, $diyBar, text) {
        progress = progress + '%';
        text = text || progress;
        var $diyProgress = $diyBar.find('.diyProgress');
        $diyProgress.width(progress).text(text);
    }
    function removeLi($li, file_id, webUploader) {
        webUploader.removeFile(file_id);
        $li.remove();
    }
    function leftLi($leftli, $li) {
        $li.insertBefore($leftli);
    }
    function rightLi($rightli, $li) {
        $li.insertAfter($rightli);
    }
    function createBox($fileInput, file, webUploader) {
        var file_id = file.id;
        var $parentFileBox = $fileInput.parents(".upload-ul");
        var file_len = $parentFileBox.children(".diyUploadHover").length;
        var li = '<li id="fileBox_' + file_id + '" class="diyUploadHover"><div class="viewThumb"><div class="diyBar"><div class="diyProgress">0%</div></div><p class="diyControl"><span class="diyLeft"><i></i></span><span class="diyCancel"><i></i></span><span class="diyRight"><i></i></span></p></div></li>';
        $parentFileBox.prepend(li);
        var $fileBox = $parentFileBox.find('#fileBox_' + file_id);
        var $diyCancel = $fileBox.find('.diyCancel').one('click',
        function() {
            removeLi($(this).parents('.diyUploadHover'), file_id, webUploader);
        });
        $fileBox.find('.diyLeft').on('click',
        function() {
            leftLi($(this).parents('.diyUploadHover').prev(), $(this).parents('.diyUploadHover'));
        });
        $fileBox.find('.diyRight').on('click',
        function() {
            rightLi($(this).parents('.diyUploadHover').next(), $(this).parents('.diyUploadHover'));
        });
        if (file.type.split("/")[0] != 'image') {
            var liClassName = getFileTypeClassName(file.name.split(".").pop());
            $fileBox.addClass(liClassName);
            return;
        }
        webUploader.makeThumb(file,
        function(error, dataSrc) {
            if (!error) {
                $fileBox.find('.viewThumb').append('<img class="viewImg" src="' + dataSrc + '" >');
            }
        });
    }
    function getFileTypeClassName(type) {
        var fileType = {};
        var suffix = '_diy_bg';
        fileType['pdf'] = 'pdf';
        fileType['ppt'] = 'ppt';
        fileType['doc'] = 'doc';
        fileType['docx'] = 'doc';
        fileType['jpg'] = 'jpg';
        fileType['zip'] = 'zip';
        fileType['rar'] = 'rar';
        fileType['xls'] = 'xls';
        fileType['xlsx'] = 'xls';
        fileType['txt'] = 'txt';
        fileType = fileType[type] || 'ppt';
        return fileType + suffix;
    }
})(jQuery);